  var rs = RedwoodSubject; //create an alias for RedwoodSubject

	var state = {}; //State object

	var Display = { //Display controller

		initialize: function() {

			$("#prob-x").text(rs.config.ProbX);
			$("#prob-y").text(1 - rs.config.ProbX);

			rs.on("next_round", function() {
				$("[name='data-round']").text(state.round + " / " + state.rounds);
				Display.replot();
			});

			$("#plot").bind("plothover", function (event, pos, item) {
				if(!state.inputsEnabled) return;
				var x = Math.max(0, pos.x);
				x = Math.min(state.maxX, x);
				var y = (state.budget - (x * state.Px)) / rs.config.Py;
				state.cursor = [x, y]
				Display.replot();
			});

			$("#plot").bind("plotclick", function (event, pos, item) {
				if(!state.inputsEnabled) return;
				var x = Math.max(0, pos.x);
				x = Math.min(state.maxX, x);
				var y = (state.budget - (x * state.Px)) / rs.config.Py;
				rs.trigger("selection", [x, y]);
			});

			$("#plot").bind("mouseout", function (event) {
				if(!state.inputsEnabled) return;
				state.cursor = undefined;
				Display.replot();
			});

			rs.on("selection", function() {
				$("#confirm-button").removeAttr("disabled");
				Display.replot();
			});

			$("#confirm-button").click(function() {
				if(!state.inputsEnabled) return;
				if(!state.selection) {
					alert("Please select a ratio of x and y by clicking on the graph.");
				} else {
					$("#confirm-button").attr("disabled", "disabled");
					rs.trigger("confirm");
				}
			});

			rs.on("confirm", function() {
				$("confirm-button").attr("disabled", "disabled");
			});

			//Display.replot();
		},

		replot: function() { //Redraw plot area

			var data = [];

			data.push([0, state.maxY]);
			data.push([state.maxX, 0]);

			var dataset = [];
			dataset.push({
				data: data,
				lines: { show: true, lineWidth: 1.5 },
				points: { show: false },
				color: "red",
			});

			if(state.cursor) {
				dataset.push({ //grey cursor dot
					data: [state.cursor],
					lines: { show: false },
					points: { show: true, fill: true, radius: 4 },
					color: "grey",
					hoverable: false
				});
			}

			if(state.selection) {
				dataset.push({ //black selection dot
					data: [state.selection],
					lines: { show: false },
					points: { show: true, lineWidth: 4, fill: true, radius: 4 },
					color: "black",
					hoverable: false
				});
				dataset.push({ //black selection dot
					data: [state.selection],
					lines: { show: false },
					points: { show: true, fill: true, radius: 1 },
					color: "black",
					hoverable: false
				});
				dataset.push({ //black selection dot
					data: [[0, state.selection[1]], state.selection, [state.selection[0], 0]],
					dashes: { show: true, lineWidth: 1 },
					color: "black",
					hoverable: false
				});

			}

			function draw_text(plot, ctx) { //display barrier and bankrupt text
				if(state.cursor) {
					var offset = plot.pointOffset({ x: state.cursor[0], y: state.cursor[1] });
					var text = "[" + state.cursor[0].toFixed(2) + ", " + state.cursor[1].toFixed(2) + "]";
					ctx.fillText(text, offset.left + 10, offset.top - 10);
				}

				if(state.selection) {
					var offset = plot.pointOffset({ x: state.selection[0], y: state.selection[1] });
					var text = "[" + state.selection[0].toFixed(2) + ", " + state.selection[1].toFixed(2) + "]";
					ctx.fillText(text, offset.left + 10, offset.top - 10);
				}
			}

			var opts = {
				grid: { clickable: true, hoverable: true },
				xaxis: { tickLength: 0, min: 0, max: state.maxX + 1 },
				yaxis: { tickLength: 0, min: 0, max: state.maxY + 1 },
				series: { shadowSize: 0 },
				hooks: { draw: [draw_text] }
			};

			$.plot("#plot", dataset, opts);
		}
	};

	rs.create = function() {

		rs.on_load(function() { //called once the page has loaded for a new sub period

			Display.initialize();

			state.rounds = rs.config.rounds || 1;
			state.round = 0;
			rs.trigger("next_round");

		});

		rs.on("next_round", function() {
			$("#confirm-button").attr("disabled", "disabled");
			state.inputsEnabled = true;

			if(state.round >= state.rounds) {
				rs.trigger("next_period");
				return;
			}

			//Begin next round
			state.round++;
			state.cursor = undefined;
			state.selection = undefined;

			state.Px = state.round > 1 ? rs.subject[rs.user_id].get("prices").x : rs.config.Px;
			state.Py = state.round > 1 ? rs.subject[rs.user_id].get("prices").y : rs.config.Py;

			state.budget = (rs.config.Ex * state.Px) + (rs.config.Ey * rs.config.Py);
			state.maxX = state.budget / state.Px;
			state.maxY = state.budget / rs.config.Py;

			state.XLimit = Math.max(state.maxX, rs.config.XLimit || 0);
			state.YLimit = Math.max(state.maxY, rs.config.YLimit || 0);

			if(rs.config.enableDefault) {
				rs.trigger("selection", [rs.config.Ex, rs.config.Ey]);
			}
		});

		rs.on("selection", function(selection) {
			state.selection = selection;
		});

		rs.on("confirm", function() {
			state.inputsEnabled = false;
			var chosen = Math.random() < rs.config.ProbX ? "x" : "y";
			rs.trigger("result", { x: state.selection[0], y: state.selection[1], chosen: chosen });

			rs.after_waiting_for_all(function() {
				var excessDemandX = 0;
				var excessDemandY = 0;
				rs.subjects.where(function() {
						return this.groupForPeriod && this.groupForPeriod === rs.subject[rs.user_id].groupForPeriod;
					}).forEach(function() {
						var selection = this.get("selection");
						excessDemandX += selection[0] - rs.config.Ex;
						excessDemandY += selection[1] - rs.config.Ey;
				});
				rs.set("prices", { x: priceUpdateFormula(state.Px, excessDemandX), y: priceUpdateFormula(state.Py, excessDemandY) });
				rs.trigger("next_round");
			});
		});

		rs.on("result", function(value) {
			if(!state.results) {
				state.results = [];
			}
			state.results.push(value);
		});

		rs.on("next_period", function() {
			var finalResult = state.results[state.results.length - 1];
			rs.set("results", finalResult);
			rs.next_period();
		});

		var priceUpdateFormula = function(currentPrice, excessDemand) {
			return Math.max(currentPrice + excessDemand * (rs.config.Z ? rs.config.Z : 0), 0.1);
		};
	};

var rs = RedwoodSubject; //create an alias for RedwoodSubject

