var rs = RedwoodSubject; //create an alias for RedwoodSubject

var state = {};

rs.create = function() {
  rs.on_load(function() {
		
		var random = Math.random(); //Clear random seed. Kept getting same value without this - weird.
		rs.trigger("selected_period", Math.floor(Math.random()*(rs.period - 1)) + 1);
		
	});
	
	rs.on("selected_period", function(period) {
		
		var results = rs.subject[rs.user_id].data["results"];
		
		var totalPoints = 0;
		
		for (var i = 0; i < results.length; i++) {
				var points = 0;
				if((i + 1) == period) {
					points = results[i].chosen === "x" ? results[i].x : results[i].y;
					rs.add_points(points);
					totalPoints += points;
				}
				
				var row = '<tr ' + (((i + 1) == period) ? 'class="warning"' : '') + '><td>' + (i + 1) + '</td><td>' + results[i].x.toFixed(2) + '</td><td>' + results[i].y.toFixed(2) + '</td><td>' + results[i].chosen + '</td><td>$' + points.toFixed(2) + '</td></tr>';
				$("#results").append(row);

		}
		
		var row = '<tr class="success" style="font-weight:bold;"><td>Total</td><td></td><td></td><td></td><td>$' + totalPoints.toFixed(2) + '</td></tr>';
		$("#results").append(row);
	});
};
